import type React from "react"
import { createContext, useContext, useRef } from "react"

import { PortalStore } from "./portal-store.js"

const PortalStoreContext = createContext<PortalStore | null>(null)

export type PortalProviderProps = {
  children: React.ReactNode
}

/**
 * Provides the PortalStore to all Portal and PortalHost components.
 * This should be rendered at the root of your application, preferably
 * in your root layout or App component before any UI that uses portals.
 */
export function PortalProvider({ children }: PortalProviderProps) {
  const storeRef = useRef<PortalStore | null>(null)

  if (!storeRef.current) {
    storeRef.current = new PortalStore()
  }

  return (
    <PortalStoreContext.Provider value={storeRef.current}>
      {children}
    </PortalStoreContext.Provider>
  )
}

/**
 * Hook to access the internal PortalStore.
 * @throws {Error} If used outside of a <PortalProvider>.
 * @returns The singleton PortalStore instance.
 */
export function usePortalStore(): PortalStore {
  const store = useContext(PortalStoreContext)

  if (!store) {
    throw new Error(
      "PortalProvider is missing. Wrap your app with <PortalProvider>.",
    )
  }

  return store
}

import { Fragment, useSyncExternalStore } from "react"
import { StyleSheet, View, type ViewProps } from "react-native"

import { usePortalStore } from "./PortalProvider.js"

export type PortalHostProps = ViewProps & {
  /**
   * The unique name of this portal host.
   * Portals with a matching `name` prop will render their content here.
   * @default "root"
   */
  name?: string
}

const DEFAULT_HOST_NAME = "root"

const styles = StyleSheet.create({
  host: {
    ...StyleSheet.absoluteFillObject,
  },
})

/**
 * A container that renders content teleported from <Portal> components.
 *
 * It is typically placed at the root of the app (top-level) to ensure
 * content (like Modals, Toasts) renders above other UI.
 *
 * By default, it fills the entire screen using absolute positioning and
 * allows touch events to pass through (`pointerEvents="box-none"`).
 */
export function PortalHost({
  name = DEFAULT_HOST_NAME,
  pointerEvents,
  style,
  children,
  ...props
}: PortalHostProps) {
  const store = usePortalStore()

  const nodes = useSyncExternalStore(
    store.subscribe,
    () => store.getSnapshot(name),
    () => store.getSnapshot(name),
  )

  return (
    <View
      pointerEvents={pointerEvents ?? "box-none"}
      style={[styles.host, style]}
      {...props}
    >
      {children}
      {nodes.map((entry) => (
        <Fragment key={entry.key}>{entry.node}</Fragment>
      ))}
    </View>
  )
}

import type React from "react"
import { useEffect, useRef } from "react"

import { usePortalStore } from "./PortalProvider.js"

export type PortalProps = {
  /**
   * The name of the PortalHost where this content should be rendered.
   * Must match the `name` prop of a mounted <PortalHost>.
   * @default "root"
   */
  name?: string
  /**
   * The content to be rendered in the portal.
   */
  children: React.ReactNode
}

let portalKey = 0

/**
 * Renders its children into a `PortalHost` with a matching name.
 * Use this to render content outside the current view hierarchy (e.g., for Modals, Tooltips).
 *
 * @example
 * <Portal name="root">
 *   <View>I am floating!</View>
 * </Portal>
 */
export function Portal({ name = "root", children }: PortalProps) {
  const store = usePortalStore()
  const keyRef = useRef<number>(0)
  const childrenRef = useRef(children)
  childrenRef.current = children

  if (keyRef.current === 0) {
    portalKey += 1
    keyRef.current = portalKey
  }

  useEffect(() => {
    store.mount(name, keyRef.current, childrenRef.current)

    return () => {
      store.unmount(name, keyRef.current)
    }
  }, [store, name])

  useEffect(() => {
    store.update(name, keyRef.current, children)
  }, [store, name, children])

  return null
}

import type { ReactNode } from "react"

export type PortalNode = {
  key: number
  node: ReactNode
}

export class PortalStore {
  private hosts = new Map<string, Map<number, ReactNode>>()
  private listeners = new Set<() => void>()

  subscribe = (listener: () => void): (() => void) => {
    this.listeners.add(listener)
    return () => {
      this.listeners.delete(listener)
    }
  }

  getSnapshot(name: string): PortalNode[] {
    const host = this.hosts.get(name)
    if (!host) {
      return []
    }

    return Array.from(host.entries()).map(([key, node]) => ({ key, node }))
  }

  mount(name: string, key: number, node: ReactNode): void {
    const host = this.ensureHost(name)
    const hadEntry = host.has(key)
    const prev = host.get(key)
    host.set(key, node)

    if (!hadEntry || prev !== node) {
      this.notify()
    }
  }

  update(name: string, key: number, node: ReactNode): void {
    const host = this.ensureHost(name)
    const prev = host.get(key)

    if (prev !== node) {
      host.set(key, node)
      this.notify()
    }
  }

  unmount(name: string, key: number): void {
    const host = this.hosts.get(name)
    if (!host) {
      return
    }

    const removed = host.delete(key)
    if (host.size === 0) {
      this.hosts.delete(name)
    }

    if (removed) {
      this.notify()
    }
  }

  private ensureHost(name: string): Map<number, ReactNode> {
    const existing = this.hosts.get(name)
    if (existing) {
      return existing
    }

    const created = new Map<number, ReactNode>()
    this.hosts.set(name, created)
    return created
  }

  private notify(): void {
    if (this.listeners.size === 0) {
      return
    }

    for (const listener of Array.from(this.listeners)) {
      listener()
    }
  }
}
